    lastName: 'torvalds',
    email: 'linustorvalds@linux.com',
    type: 'staff',
    isAdmin: true,
    password: '123456b',
  },
};

// global.Users = {};

global.Accounts = {
  accountNumber: {
    accountNumber: 2000000000,
    firstName: 'nawas',
    lastName: 'adam',
    email: 'nawasnaziru@gmail.com',
    type: 'savings',
    openingBalance: 0,
    createdOn: '2019-04-11T09:10:33.000Z',
    owner: 0,
    status: 'active',
    balance: 0,
    id: 0,
  },
};

global.nextAccount = 2000000000;
global.Transactions = {};

global.staffIds = [104, 189, 316, 427, 518];
global.adminIds = [411, 581, 611, 723, 851];

app.use(logger('dev'));
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: false }));
app.use(cookieParser());

app.use(express.static(path.join(__dirname, 'UI')));

app.use('/api/v1', router);

var config = {
  user: 'postgres',
  database: 'Banka', 
  password: 'zazaa1992', 
  port: 5432, 
  max: 10, // max number of connection can be open to database
  idleTimeoutMillis: 30000, // how long a client is allowed to remain idle before being closed
};
	
var pool = new pg.Pool(config);
 
app.get('/pool', (req, res, next) => {
    pool.connect((err,client,done) => {
       if(err){
           console.log("not able to get connection due to " + err);
           res.status(400).send(err);
           return;
       } 
       client.query('SELECT * FROM student where id = $1', [1],(err,result) => {
           done(); // closing the connection;
           if(err){
               console.log(err);
               res.status(400).send(err);
           }
           res.status(200).send(result.rows);
       });
    });
});

app.use((req, res) => {
  res.sendFile(path.join(__dirname, 'UI', 'index.html'));
});

// catch 404 and forward to error handler
app.use((req, res, next) => {
  const err = new Error('Not Found');
  err.status = 404;
  next(err);
});

// error handlers
// Catch unauthorised errors
app.use((err, req, res) => {
  if (err.name === 'UnauthorizedError') {
    res.status(401);
    res.json({ message: `${err.name}: ${err.message}` });
  }
});

// development error handler
// will print stacktrace
if (app.get('env') === 'development') {
  app.use((err, req, res) => {
    res.status(err.status || 500);
    res.render('error', {
      message: err.message,
      error: err,
    });
  });
}

// production error handler
// no stacktraces leaked to user
app.use((err, req, res) => {
  res.status(err.status || 500);
  res.render('error', {
    message: err.message,
    error: {},
  });
});

// start server

app.set('port', process.env.PORT || 3000);

const server = app.listen(app.get('port'), () => {
  debug('Express server listening on port ' + server.address().port);
 console.log('Express server listening on localhost:3000');
});

export default app;

